/* CSS imports must be first */
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

/* Chamfered corners utility */
.chamfered {
  --chamfer: 12px;
  clip-path: polygon(
    var(--chamfer) 0%, 
    calc(100% - var(--chamfer)) 0%, 
    100% var(--chamfer), 
    100% calc(100% - var(--chamfer)), 
    calc(100% - var(--chamfer)) 100%, 
    var(--chamfer) 100%, 
    0% calc(100% - var(--chamfer)), 
    0% var(--chamfer)
  );
}


:root {
  /* Industrial color palette: black text, orange highlights, white/gray */
  --background: hsl(0 0% 100%);
  --foreground: hsl(0 0% 0%); /* Pure black text */
  --card: hsl(0 0% 100%);
  --card-foreground: hsl(0 0% 0%);
  --popover: hsl(0 0% 100%);
  --popover-foreground: hsl(0 0% 0%);
  --primary: hsl(22 100% 50%); /* Industrial orange */
