@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer base {
  /* Industrial typography */
  h1, h2, h3, h4, h5, h6 {
    @apply font-bold tracking-tight;
  }

  h1 {
    @apply text-4xl font-black;
  }

  h2 {
    @apply text-3xl font-bold;
  }

  h3 {
    @apply text-2xl font-bold;
  }

  /* Override all rounded corners with hard edges */
  * {
    border-radius: 0 !important;
  }

  /* Button base styles with hard edges */
  button {
    @apply font-medium;
    border-radius: 0 !important;
  }

  /* Input base styles */
  input, textarea, select {
    @apply border-2 font-medium;
    border-radius: 0 !important;
  }

  /* Card and container base styles */
  .card, [role="dialog"] {
    border-radius: 0 !important;
    @apply shadow-md;
  }
}

@layer utilities {
  .gradient-bg {
    background: linear-gradient(135deg, hsl(0 0% 15%) 0%, hsl(0 0% 25%) 100%);
  }

  .industrial-card {
    background: hsl(0 0% 100%);
    border: 2px solid hsl(0 0% 85%);
    box-shadow: 0px 4px 6px -1px hsl(0 0% 0% / 0.1);
  }

  .industrial-button {
    @apply bg-primary text-primary-foreground font-bold py-3 px-6 uppercase tracking-wide transition-all duration-200;
    border: none;
    box-shadow: none;
  }

  .industrial-button:hover {
    background-color: hsl(22 100% 45%);
  }

  .industrial-button:active {
    background-color: hsl(22 100% 40%);
  }

  .industrial-input {
    @apply border-2 border-border bg-input text-foreground font-medium py-3 px-4 focus:border-primary focus:ring-2 focus:ring-primary/20 transition-all;
    outline: none;
  }

  .text-highlight {
    @apply bg-primary text-primary-foreground px-2 py-1 font-bold;
  }

  .animate-fade-in {
    animation: fadeIn 0.6s ease-out;
  }

  @keyframes fadeIn {
    from { 
      opacity: 0; 
      transform: translateY(10px); 
    }
    to { 
      opacity: 1; 
      transform: translateY(0); 
    }
  }

  .animate-slide-in {
    animation: slideIn 0.4s ease-out;
  }

  @keyframes slideIn {
    from { 
      opacity: 0; 
      transform: translateX(-20px); 
    }
    to { 
      opacity: 1; 
      transform: translateX(0); 
    }
  }

  /* Industrial loading spinner with hard edges */
  .industrial-spinner {
    display: inline-block;
    position: relative;
    width: 32px;
    height: 32px;
  }

  .industrial-spinner::before,
  .industrial-spinner::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border: 3px solid transparent;
    border-top: 3px solid currentColor;
    border-right: 3px solid currentColor;
    animation: industrialSpin 1s linear infinite;
  }

  .industrial-spinner::after {
    border-top: 3px solid transparent;
    border-right: 3px solid transparent;
    border-bottom: 3px solid currentColor;
    border-left: 3px solid currentColor;
    animation-delay: 0.5s;
  }

  @keyframes industrialSpin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

  /* Small variant */
  .industrial-spinner-sm {
    width: 16px;
    height: 16px;
  }

  .industrial-spinner-sm::before,
  .industrial-spinner-sm::after {
    border-width: 2px;
  }

  /* Large variant */
  .industrial-spinner-lg {
    width: 48px;
    height: 48px;
  }

  .industrial-spinner-lg::before,
  .industrial-spinner-lg::after {
    border-width: 4px;
  }
}

